import React,{ Component,Fragment } from 'react'
import { 
    Row,
    Col,
    Collapse,
    Form,
    Input,
    Switch,
    Select,
    InputNumber
} from 'antd'
import { connect } from 'react-redux'
import { changeItem,changeItemArr } from '../../../store/actions/screen-actions'
import ColorPicker from '../ColorPicker'
const { Panel } = Collapse;
const { Option } = Select;
class SettingPropsForm extends Component{
    colorChange = (value,event)=>{
        console.log(value)
    }
    moduleStyleWidthChange = (e)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                style:{
                    width:e.target.value
                }
            }
        })
    }
    moduleStyleHeightChange = (e)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                style:{
                    height:e.target.value
                }
            }
        })
    }
    moduleStyleXChange = (e)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                style:{
                    x:e.target.value
                }
            }
        })
    }
    moduleStyleYChange = (e)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                style:{
                    y:e.target.value
                }
            }
        })
    }
    switchBackground = (checked)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                style:{
                    background:checked?"#ff0000":"transparent"
                }
            }
        })
    }
    moduleStyleBackgroundChange = (color)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                style:{
                    background:color.hex
                }
            }
        })
    }
    moduleTitleShowChange = (checked)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                options:{
                    title:{
                        show:checked
                    }
                }
            }
        })
    }
    moduleOptionsTitleTextChange = (e)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                options:{
                    title:{
                        text:e.target.value
                    }
                }
            }
        })
    }
    moduleOptionsTitlePositionChange = value=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                options:{
                    title:{
                        left:value
                    }
                }
            }
        })
    }
    moduleOptionsTitleFontSizeChange = value=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                options:{
                    title:{
                        textStyle:{
                            fontSize:value
                        }
                    }
                }
            }
        })
    }
    moduleOptionsTitleColorChange = color=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                options:{
                    title:{
                        textStyle:{
                            color:color.hex
                        }
                    }
                }
            }
        })
    }
    moduleLegendShowChange = (checked)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                options:{
                    legend:{
                        show:checked
                    }
                }
            }
        })
    }
    moduleOptionsLegendFontSizeChange = (value)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                options:{
                    legend:{
                        textStyle:{
                            fontSize:value
                        }
                    }
                }
            }
        })
    }
    moduleOptionsLegendColorChange = (color)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                options:{
                    legend:{
                        textStyle:{
                            color:color.hex
                        }
                    }
                }
            }
        })
    }
    moduleDataSmoothChange = (checked)=>{
        const { id } = this.props.module
        this.props.module.options.series.map(item=>{
            item.smooth = checked
        })
        this.props.changeItemArr({
            id,
            path:["options","series"],
            data:this.props.module.options.series
        })
    }
    moduleLabelShowChange = (checked)=>{
        const { id } = this.props.module
        this.props.module.options.series.map(item=>{
            item.label.show = checked
        })
        this.props.changeItemArr({
            id,
            path:["options","series"],
            data:this.props.module.options.series
        })
    }
    moduleOptionsLabelFontSizeChange = (value)=>{
        const { id } = this.props.module
        this.props.module.options.series.map(item=>{
            item.label.fontSize = value
        })
        this.props.changeItemArr({
            id,
            path:["options","series"],
            data:this.props.module.options.series
        })
    }
    moduleOptionsLabelOffsetXChange = (value)=>{
        const { id } = this.props.module
        this.props.module.options.series.map(item=>{
            item.label.offset[0] = value
        })
        this.props.changeItemArr({
            id,
            path:["options","series"],
            data:this.props.module.options.series
        })
    }
    moduleOptionsLabelOffsetYChange = (value)=>{
        const { id } = this.props.module
        this.props.module.options.series.map(item=>{
            item.label.offset[1] = value
        })
        this.props.changeItemArr({
            id,
            path:["options","series"],
            data:this.props.module.options.series
        })
    }
    render(){
        const { style,options } = this.props.module
        console.log(options)
        return (
            <div className="setting-form-wrap">
                <Form>
                    <Collapse bordered={false}>
                        <Panel header="" key="1">
                            <Row gutter={10}>
                                <Col span={6}>
                                    <label htmlFor=""></label>
                                </Col>
                                <Col span={9}>
                                    <Form.Item>
                                        <Input
                                            suffix="w" 
                                            value={style.width}
                                            onChange={this.moduleStyleWidthChange}
                                        />
                                    </Form.Item>
                                </Col>
                                <Col span={9}>
                                    <Form.Item>
                                        <Input 
                                            suffix="h" 
                                            value={style.height}
                                            onChange={this.moduleStyleHeightChange}
                                        />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={10}>
                                <Col span={6}>
                                    <label htmlFor=""></label>
                                </Col>
                                <Col span={9}>
                                    <Form.Item>
                                        <Input 
                                            suffix="x" 
                                            value={style.x}
                                            onChange={this.moduleStyleXChange}
                                        />
                                    </Form.Item>
                                </Col>
                                <Col span={9}>
                                    <Form.Item>
                                        <Input 
                                            suffix="y" 
                                            value={style.y}
                                            onChange={this.moduleStyleYChange}
                                        />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={10}>
                                <Col span={6}>
                                    <label htmlFor=""></label>
                                </Col>
                                <Col span={9}>
                                    <InputNumber 
                                        min={0}
                                        value={options.legend.textStyle.fontSize} 
                                        onChange={this.moduleOptionsLegendFontSizeChange} 
                                    />
                                </Col>
                                <Col span={9}>
                                    <InputNumber 
                                        min={0}
                                        value={options.legend.textStyle.fontSize} 
                                        onChange={this.moduleOptionsLegendFontSizeChange} 
                                    />
                                </Col>
                                <Col span={9} push={6}>
                                    <InputNumber 
                                        min={0}
                                        value={options.legend.textStyle.fontSize} 
                                        onChange={this.moduleOptionsLegendFontSizeChange} 
                                    />
                                </Col>
                                <Col span={9}>
                                    <InputNumber 
                                        min={0}
                                        value={options.legend.textStyle.fontSize} 
                                        onChange={this.moduleOptionsLegendFontSizeChange} 
                                    />
                                </Col>
                            </Row>
                            <Row gutter={10}>
                                <Col span={6}>
                                    <label htmlFor=""></label>
                                </Col>
                                <Col span={18}>
                                    <Switch 
                                        checked={style.background!=='transparent'}
                                        style={{marginTop:5}}
                                        onChange={this.switchBackground}
                                    />
                                </Col>
                                {
                                    style.background==="transparent"?
                                    ""
                                    :
                                    <Col span={18} push={6}>
                                        <ColorPicker 
                                            value={style.background}
                                            onChange={this.moduleStyleBackgroundChange}
                                        />
                                    </Col>
                                }
                            </Row>
                        </Panel>
                        <Panel header="" key="2">
                            <Row gutter={[10,10]}>
                                <Col span={7}>
                                    <label htmlFor=""></label>
                                </Col>
                                <Col span={17}>
                                    <Form.Item>
                                        <Switch
                                            checked={options.title.show}
                                            onChange={this.moduleTitleShowChange}
                                        />
                                    </Form.Item>
                                </Col>
                                {
                                    options.title.show?<Fragment>
                                        <Col span={7}>
                                            <label htmlFor=""></label>
                                        </Col>
                                        <Col span={17}>
                                            <Form.Item>
                                                <Input 
                                                    value={options.title.text}
                                                    onChange={this.moduleOptionsTitleTextChange}
                                                />
                                            </Form.Item>
                                        </Col>
                                        <Col span={7}>
                                            <label htmlFor=""></label>
                                        </Col>
                                        <Col span={17}>
                                            <Form.Item>
                                                <Select
                                                    placeholder=""
                                                    value={options.title.left}
                                                    dropdownClassName="dark-style"
                                                    onChange={this.moduleOptionsTitlePositionChange}
                                                >
                                                    <Option value="left"></Option>
                                                    <Option value="center"></Option>
                                                    <Option value="right"></Option>
                                                </Select>
                                            </Form.Item>
                                        </Col>
                                        <Col span={7}>
                                            <label htmlFor=""></label>
                                        </Col>
                                        <Col span={17}>
                                            <Form.Item>
                                                <InputNumber 
                                                    min={12}
                                                    style={{width:174}}
                                                    value={options.title.textStyle.fontSize} 
                                                    onChange={this.moduleOptionsTitleFontSizeChange} 
                                                />
                                            </Form.Item>
                                        </Col>
                                        <Col span={7}>
                                            <label htmlFor=""></label>
                                        </Col>
                                        <Col span={17}>
                                            <Form.Item>
                                                <ColorPicker 
                                                    value={options.title.textStyle.color}
                                                    onChange={this.moduleOptionsTitleColorChange}
                                                />
                                            </Form.Item>
                                        </Col>
                                    </Fragment>:""
                                }
                            </Row>
                        </Panel>
                        <Panel header="" key="3">
                            <Row gutter={[10,10]}>
                                <Col span={7}>
                                    <label htmlFor=""></label>
                                </Col>
                                <Col span={17}>
                                    <Form.Item>
                                        <Switch
                                            checked={options.legend.show}
                                            onChange={this.moduleLegendShowChange}
                                        />
                                    </Form.Item>
                                </Col>
                                {
                                    options.legend.show?<Fragment>
                                        <Col span={7}>
                                            <label htmlFor=""></label>
                                        </Col>
                                        <Col span={17}>
                                            <Form.Item>
                                                <InputNumber 
                                                    min={12}
                                                    style={{width:174}}
                                                    value={options.legend.textStyle.fontSize} 
                                                    onChange={this.moduleOptionsLegendFontSizeChange} 
                                                />
                                            </Form.Item>
                                        </Col>
                                        <Col span={7}>
                                            <label htmlFor=""></label>
                                        </Col>
                                        <Col span={17}>
                                            <Form.Item>
                                                <ColorPicker 
                                                    value={options.legend.textStyle.color}
                                                    onChange={this.moduleOptionsLegendColorChange}
                                                />
                                            </Form.Item>
                                        </Col>
                                    </Fragment>:""
                                }
                            </Row>
                        </Panel>
                        <Panel header="" key="4">
                            <Row gutter={[10,10]}>
                            <Col span={7}>
                                    <label htmlFor=""></label>
                                </Col>
                                <Col span={17}>
                                    <Form.Item>
                                        <Switch
                                            checked={options.series[0].smooth}
                                            onChange={this.moduleDataSmoothChange}
                                        />
                                    </Form.Item>
                                </Col>
                                <Col span={7}>
                                    <label htmlFor=""></label>
                                </Col>
                                <Col span={17}>
                                    <Form.Item>
                                        <Switch
                                            checked={options.series[0].label.show}
                                            onChange={this.moduleLabelShowChange}
                                        />
                                    </Form.Item>
                                </Col>
                                {
                                    options.series[0].label.show?<Fragment>
                                        <Col span={7}>
                                            <label htmlFor=""></label>
                                        </Col>
                                        <Col span={17}>
                                            <Form.Item>
                                                <InputNumber 
                                                    min={12}
                                                    style={{width:174}}
                                                    value={options.series[0].label.fontSize} 
                                                    onChange={this.moduleOptionsLabelFontSizeChange} 
                                                />
                                            </Form.Item>
                                        </Col>
                                        <Col span={7}>
                                            <label htmlFor=""></label>
                                        </Col>
                                        <Col span={17}>
                                            <InputNumber
                                                style={{width:80}}
                                                value={options.series[0].label.offset[0]} 
                                                onChange={this.moduleOptionsLabelOffsetXChange} 
                                            />
                                            <InputNumber
                                                style={{width:80,marginLeft:12}}
                                                value={options.series[0].label.offset[1]} 
                                                onChange={this.moduleOptionsLabelOffsetYChange} 
                                            />
                                        </Col>
                                    </Fragment>:""
                                }
                            </Row>
                        </Panel>
                        <Panel header="X" key="5">

                        </Panel>
                        <Panel header="Y" key="6">
                            <div style={{color:"#fff"}}>Y</div>
                        </Panel>
                        <Panel header="" key="7">
                            <div style={{color:"#fff"}}></div>
                        </Panel>
                    </Collapse>
                </Form>
            </div>
        )
    }
}
const mapStateToProps = (state,ownProps)=>{
    return {

    }
}
const mapDispatchToProps = dispatch=>{
    return {
        changeItem(data){
            const action = changeItem(data)
            dispatch(action)
        },
        changeItemArr(data){
            const action = changeItemArr(data)
            dispatch(action)
        }
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(SettingPropsForm)
